Script.core:
import VR glove = VR.find("vrpn_device")
if not glove: return
slider1 = 100 #index finger slider2 = 200 #middle finger slider3 = 300 #ring finger slider4 = 400 #small finger
startBent = 0 endBent = 0
value1 = glove.getSlider(slider1) value2 = glove.getSlider(slider2) value3 = glove.getSlider(slider3) value4 = glove.getSlider(slider4)
def checkSlider(sliderNo): return (glove.getSlider(sliderNo) > startBent and glove.getSlider(sliderNo) < endBent)
fistPose = True for value in [value1, value2, value3, value4]: fistPose = fistPose and checkSlider(value)
if (fistPose): VR.introduction.setVisible(False)

import VR
if not hasattr(VR, "debuggingContainer"): VR.debuggingContainer = VR.Object('debugging') VR.scene.addChild(VR.debuggingContainer)
# Create small debugging spheres on all coordinates stored in the # array nodes. nodes has to be an array of three dimensional arrays, # each containing the coordinates of the spheres. # The spheres will be created as children of VR.scene def testCircles(nodes): for idx, node in enumerate(nodes): circ = VR.Geometry("debug" + str(node[0])+str(node[1])+str(node[2])) circ.setPrimitive("Sphere 0.01") circ.setFrom(node) VR.debuggingContainer.addChild(circ) VR.testCircles = testCircles
